import { useLocation, Link } from "react-router-dom"
import styles from './MenuLink.modules.css'

const MenuLink = () => {

    const location = useLocation()

    return(
        <Link className={
            `${styles.link}
            ${location.pathname === '/' ? styles.linkDestacado : styles.link}`
        } to={'/'}>
            Home
        </Link>
    )
}

export default MenuLink
