    const navigation = useNavigate()

    return (
        <Link to={`posts/${post.id}`}>
            <div className={styles.post}>
                <img
                    className={styles.capa}
                    src={`/assets/posts/${post.id}/capa.png`}
                    alt='Imagem da capa do post'
